mapper.select:
select t.t_id, t.t_themename, t.t_author, t.t_firmware, t.t_registerdate, t.t_downloadnum, t.t_price, t.t_discount, t.t_status,t_vip, t.t_abstract, t.t_picpath, b.b_brandname
,COUNT(c_id) AS collecstatus
from theme t LEFT JOIN brand b ON t.b_id = b.b_id
JOIN collection c ON c.t_id = t.t_id
where t.t_id = #{t_id}
AND c.s_id = #{s_id}

 		SELECT COUNT(*)
		FROM theme T
		JOIN assets A
		ON A.t_id = T.t_id
		WHERE s_id = #{s_id}
		AND A.t_id = #{t_id}
